import { Card, CardContent, Grid, Typography, styled } from "@mui/material"; 

const Image = styled('img')({
    height: 268,
    width: '88%',
    objectFit: 'cover',
    borderRadius: 5

});

const Component = styled(Card)`
    margin-bottom: 20px;
    box-shadow:0 2px 5px 0 rgb(0 0 0/16%), 0 2px 10px 0 rgb(0 0 0 /12%);
`;

const Container = styled(CardContent)`
    padding: 8px;
    padding-bottom: 4px !important;
`;

const Text = styled(Typography)`
    font-weight: 300;
    font-size: 22px;
    color: #44444d;
    line-height:27px;
`;

const RightContainer = styled(Grid)(({theme}) => ({
    margin: '5px 0 0 -25px',
    display:'flex',
    flexDirection:'column',
    [theme.breakpoints.between('sm', 'lg')]:{
        padding: '0 5px'
    },
    [theme.breakpoints.down('sm')]:{
        margin: '5px 0'
    }
}));

const Author = styled(Typography)`
    color: #808290;
    font-size: 12px;
    line-height: 22px;
`;

const Description = styled(Typography)`
    line-height: 22px;
    color: #44444d;
    margin-top:5px;
`

const Publisher = styled(Typography)`
    font-size: 12px;
    margin-top: auto;
    margin-bottom: 10px

`;

const Article = ({ data }) => {
    return(
        <Component>
            <Container>
                <Grid container>
                    <Grid lg={5} sm={5} xs={12} item>
                        <Image src={data.urlToImage} alt="image" />
                    </Grid>

                    <RightContainer lg={7} sm={7} xs={12} item>
                        <Text>{data.title}</Text>
                        <Author> 
                            <b>short</b> by {data.author} / { new Date(data.publishedAt).toDateString()}
                        </Author>
                        <Description>{data.description}</Description>
                        <Publisher>
                            <b>read more at</b>
                            <a style={{textDecoration: 'none', color: '#000'}} href={data.url} target="_blank"> <b>{data.publisher}</b></a>
                            </Publisher>
                    </RightContainer>
                </Grid>
            </Container>
        </Component>
    )
}

export default Article;
